mapper.select:
SELECT * FROM( SELECT D.RANK RK , D.* FROM( SELECT DENSE_RANK() OVER (ORDER BY A.INQUIRY_NO DESC) RANK , A.INQUIRY_NO , A.MEMBER_ID , A.INQUIRY_TITLE , A.INQUIRY_CONTENT , A.INQUIRY_TYPE , A.INQUIRY_REG_DATE , A.INQUIRY_ANSWERED_YN FROM TBL_INQUIRY A LEFT JOIN TBL_ATTACHMENT B ON(A.INQUIRY_NO = B.REF_INQ_NO) LEFT JOIN TBL_COMMENT C ON(A.INQUIRY_NO = C.REF_POST_NO) WHERE 1 = 1
AND A.MEMBER_ID = #{ memberId } ) D
WHERE D.RANK <= #{ criteria.currentPageNo } * #{ criteria.recordsPerPage }
) E
WHERE E.RK > (#{ criteria.currentPageNo } - 1) * #{ criteria.recordsPerPage }
