mapper.insert:
INSERT
  INTO TBL_ROLE
(
  MEMBER_ID
, AUTHORITY_CODE
)
VALUES
(
  #{ memberId }
, #{ authorityCode }
)

INSERT
  INTO TBL_WISHLIST
(
  MEMBER_ID
, OPTION_NO
)
VALUES
(
  #{ memberId }
, #{ optionNo }
)

mapper.delete:
DELETE
  FROM TBL_WISHLIST
 WHERE MEMBER_ID = #{ memberId }
   AND OPTION_NO = #{ optionNo }
