mapper.insert:
INSERT
  INTO TBL_ORDER_DETAIL
(
  ORDER_NO
, MEMBER_ID
, ORDER_DATE
, RCVR_NAME
, RCVR_PHONE
, RCVR_ADDRESS
, DLVR_REQ_MESSAGE
, DLVR_STATUS
)
VALUES
(
  #{ orderNo }
, #{ memberId }
, #{ orderDate }
, #{ rcvrName }
, #{ rcvrPhone }
, #{ rcvrAddress }
, #{ dlvrReqMessage }
, #{ dlvrStatus }
)

INSERT
  INTO TBL_ORDER
(
  ORDER_NO
, OPTION_NO
, ORDER_QUANTITY
, ORDER_AMOUNT
)
VALUES
(
  #{ orderNo }
, #{ optionNo }
, #{ orderQuantity }
, #{ orderAmount }
)

INSERT
  INTO TBL_POINT
(
  POINT_NO
, PAYMENT_NO
, BONUS_REASON
, POINT_AMOUNT
, POINT_DATETIME
, POINT_STATUS
)
VALUES
(
  SEQ_POINT_NO.NEXTVAL
, #{ paymentNo }
, NULL
, #{ pointAmount }
, #{ pointDateTime }
, #{ pointStatus }
)

INSERT
  INTO TBL_PAYMENT
(
  PAYMENT_NO
, ORDER_NO
, PAYMENT_METHOD
, PAYMENT_AMOUNT
, PAYMENT_DATETIME
)
VALUES
(
  #{ paymentNo }
, #{ orderNo }
, #{ paymentMethod }
, #{ paymentAmount }
, #{ paymentDateTime }
)

INSERT
  INTO TBL_POINT
(
  POINT_NO
, PAYMENT_NO
, BONUS_REASON
, POINT_AMOUNT
, POINT_DATETIME
, POINT_STATUS
)
VALUES
(
  SEQ_POINT_NO.NEXTVAL
, #{ paymentNo }
, NULL
, #{ pointAmount }
, #{ pointDateTime }
, #{ pointStatus }
)
