mapper.insert:
INSERT
  INTO TBL_CATEGORY
(
  CATEGORY_NO
, CATEGORY_NAME
, CATEGORY_SECTION
)
VALUES
(
  (SELECT MAX(CATEGORY_NO)+1 FROM TBL_CATEGORY)
, #{ categoryName }
, #{ categorySection }
)

INSERT
  INTO TBL_PRODUCT
(
  PROD_NO
, CATEGORY_NO
, BRAND_NO
, PROD_NAME
, PROD_DESC
, PRODUCT_TAG
, DISCOUNT_RATE
, PROD_PRICE
, PROD_ORIGIN
, PROD_DETAIL_CONTENT
, PROD_DETAIL_VIEW_COUNT
, PROD_ENROLL_DATE
, PROD_CHANGE_DATE
, PROD_AVAIL_YN
)
VALUES
(
  SEQ_PROD_NO.NEXTVAL
, #{ categoryNo }
, #{ brandNo }
, #{ prodName }
, #{ prodDesc }
, #{ productTag }
, #{ discountRate }
, #{ prodPrice }
, #{ prodOrigin }
, #{ prodDetailContent }
, DEFAULT
, SYSDATE
, NULL
, DEFAULT
)

INSERT
  INTO TBL_ATTACHMENT
(
  ATTACHMENT_NO
, REF_PROD_NO
, REF_REVW_NO
, ORIG_FILE_NAME
, SAVE_FILE_NAME
, SAVE_PATH
, THUMBNAIL_PATH
, FILE_TYPE
, ATTACH_STATUS_YN
)
VALUES
(
  SEQ_ATTACH_NO.NEXTVAL
, #{ refProdNo }
, NULL
, #{ origFileName }
, #{ saveFileName }
, #{ savePath }
, #{ thumbnailPath }
, #{ fileType }
, DEFAULT
)

mapper.select:
SELECT E.PROD_NO , E.CATEGORY_NAME , E.BRAND_NAME , E.PROD_NAME , E.PROD_DESC , E.PRODUCT_TAG , E.DISCOUNT_RATE , E.PROD_PRICE , E.PROD_ORIGIN , E.PROD_DETAIL_CONTENT , E.PROD_DETAIL_VIEW_COUNT , E.PROD_ENROLL_DATE , E.PROD_CHANGE_DATE , E.PROD_AVAIL_YN , E.OPT_BODY_COLOR , E.OPT_INK_COLOR , E.OPT_POINT_SIZE FROM ( SELECT D.RANK RK , D.PROD_NO , D.CATEGORY_NAME , D.BRAND_NAME , D.PROD_NAME , D.PROD_DESC , D.PRODUCT_TAG , D.DISCOUNT_RATE , D.PROD_PRICE , D.PROD_ORIGIN , D.PROD_DETAIL_CONTENT , D.PROD_DETAIL_VIEW_COUNT , D.PROD_ENROLL_DATE , D.PROD_CHANGE_DATE , D.PROD_AVAIL_YN , D.OPT_BODY_COLOR , D.OPT_INK_COLOR , D.OPT_POINT_SIZE FROM (SELECT DENSE_RANK() OVER (ORDER BY PROD_ENROLL_DATE) RANK , A.PROD_NO , B.CATEGORY_NAME , C.BRAND_NAME , A.PROD_NAME , A.PROD_DESC , A.PRODUCT_TAG , A.DISCOUNT_RATE , A.PROD_PRICE , A.PROD_ORIGIN , A.PROD_DETAIL_CONTENT , A.PROD_DETAIL_VIEW_COUNT , A.PROD_ENROLL_DATE , A.PROD_CHANGE_DATE , A.PROD_AVAIL_YN , O.OPT_BODY_COLOR , O.OPT_INK_COLOR , O.OPT_POINT_SIZE FROM TBL_PRODUCT A LEFT JOIN TBL_CATEGORY B ON(A.CATEGORY_NO = B.CATEGORY_NO) LEFT JOIN TBL_BRAND C ON(A.BRAND_NO = C.BRAND_NO) LEFT JOIN TBL_OPTION O ON(A.PROD_NO = O.REF_PROD_NO) ) D WHERE D.RANK <= #{ currentPageNo } * #{ recordsPerPage }
D.PROD_NAME LIKE '%' || #{ keyword } || '%' D.BRAND_NAME LIKE '%' || #{ keyword } || '%' D.CATEGORY_NAME LIKE '%' || #{ keyword } || '%' D.OPT_BODY_COLOR LIKE '%' || #{ keyword } || '%' D.OPT_INK_COLOR LIKE '%' || #{ keyword } || '%' D.OPT_POINT_SIZE LIKE '%' || #{ keyword } || '%' D.PRODUCT_TAG LIKE '%' || #{ keyword } || '%'
) E WHERE E.RK > (#{ currentPageNo } - 1) * #{ recordsPerPage }

mapper.update:
UPDATE
	   TBL_PRODUCT A
   SET A.PROD_DETAIL_VIEW_COUNT = (SELECT
   									      B.PROD_DETAIL_VIEW_COUNT
   									 FROM TBL_PRODUCT B
   									WHERE B.PROD_NO = #{ prodNo }
   								   ) + 1
 WHERE A.PROD_NO = #{ prodNo }

mapper.delete:

DELETE
  FROM TBL_PRODUCT
 WHERE PROD_NO = #{ prodNo }
