mapper.insert:
INSERT
  INTO TBL_REVIEW
(
  REVIEW_NO
, OPTION_NO
, ORDER_NO
, MEMBER_ID
, REVW_TITLE
, REVW_CONTENT
, REVW_REG_DATE
, REVW_HITS
, REVW_RATINGS
)
VALUES
(
  SEQ_REVIEW_NO.NEXTVAL
, #{ optionNo }
, #{ orderNo }
, #{ memberId }
, #{ revwTitle }
, #{ revwContent }
, SYSDATE
, DEFAULT
, #{ revwRatings }
)

INSERT
  INTO TBL_ATTACHMENT
(
  ATTACHMENT_NO
, REF_PROD_NO
, REF_REVW_NO
, ORIG_FILE_NAME
, SAVE_FILE_NAME
, SAVE_PATH
, THUMBNAIL_PATH
, FILE_TYPE
, ATTACH_STATUS_YN
)
VALUES
(
  SEQ_ATTACH_NO.NEXTVAL
, NULL
, #{ refRevwNo }
, #{ origFileName }
, #{ saveFileName }
, #{ savePath }
, #{ thumbnailPath }
, #{ fileType }
, DEFAULT
)

mapper.update:
UPDATE
	   TBL_REVIEW A
   SET A.REVW_HITS = (SELECT
   							 B.REVW_HITS
   						FROM TBL_PRODUCT B
   					   WHERE B.REVIEW_NO = #{ reviewNo }
   					  ) + 1
 WHERE A.REVIEW_NO = #{ reviewNo }
